
#cp /etc/kibana/kibana.yml kib.yml
cp kibana.yml kib.yml
strMachines="elasticsearch.hosts"$'\n'
esmachines=()
esmachines+=('http://elasticsearch1:9200')
esmachines+=('http://elasticsearch2:9200')

nl=''
for i in "${esmachines[@]}"
do
   strMachines=${strMachines}$nl$'   - '${i}
   nl=$'\n'
   # or do whatever with individual element of the array
done
echo "$strMachines" >>kib.yml
#sed -i -e "/#elasticsearch.hosts/s/#elasticsearch\.hosts\:[ ]*\[[\"a-z\:\/0-9\.]*\]/#replaced elasticsearch.hosts line /" kib.yml
# turn on safe contnt security polity
echo "csp.strict: true" >>kib.yml

