#!/bin/bash
node app_index.js -s sampleservice -c appserviceconfig.json&
echo kill -9 $! >./killsampleservices.sh

node app_index.js -s sampleserviceappkey -c appserviceconfig.json&
echo kill -9 $! >>./killsampleservices.sh

node app_index.js -s sampleservicebasic -c appserviceconfig.json&
echo kill -9 $! >>./killsampleservices.sh

node app_index.js -s sampleserviceacckey -c appserviceconfig.json&
echo kill -9 $! >>./killsampleservices.sh

node systemservice_index.js -s keymgr -i "{\"appdir\":\"$pwd\",\"aclconfig\":\"aclconfig.json\",\"secret\":\"helloworld\", \"appserviceconfig\":\"appserviceconfig.json\"}"&
echo kill -9 $! >>./killsampleservices.sh

node app_index.js -s taskserver -c appserviceconfig.json&
echo kill -9 $! >>./killsampleservices.sh
